mapper.delete:
delete
from tb_buycar
where g_id = #{g_id} and u_id=#{u_id}

mapper.select:
select c_number 
from tb_buycar
where u_id=#{u_id} and g_id=g.#{u_id}

mapper.update:
update tb_buycar 
set c_number = c_number-1 
where u_id=#{u_id} and g_id=g.#{u_id}

update tb_buycar 
set c_number = c_number+1 
where u_id=#{u_id} and g_id=g.#{u_id}
