mapper.select:
SELECT g_name,g_intro,g_price,g_total,uptime,downtime,g_status,t_name,g_picture
FROM `tb_goods` as g,tb_type as t
where g.t_id=t.t_id and t.t_name=#{t_name}

mapper.update:
update tb_goods
set t_id = #{t_id},g_name = #{g_name},g_intro = #{g_intro},g_price =
#{g_price},
g_total=#{g_total},g_picture=#{g_picture}
where g_id=#{g_id}

update tb_goods
set downtime = NOW(),g_status=0
where g_id=#{g_id}
